import { useState, useEffect, Fragment, useContext, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import toast from "react-hot-toast";

import useFetch from "../hooks/UseFetch";
import Context from "../state/Context";
import { comic, comicImage } from "../api";

function Read() {
  const { width } = useContext(Context);
  const navigate = useNavigate();
  const params = useParams();
  const [data] = useFetch(`${comic}/${params?.slug}`);
  const [dataChapter] = useFetch(`${comicImage}/${params?.id}`);
  const [images, setImages] = useState([]);
  const [chapter, setChapter] = useState([]);
  const [chapterPath, setChapterPath] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    if (data?.data?.item?.chapters[0]?.server_data) {
      const chaptersId = data.data.item.chapters[0].server_data.map(
        chapter => chapter.chapter_api_data.split("/").pop()
      );
      const index = chaptersId.findIndex(id => id === params.id);
      setChapter(chaptersId);
      setCurrentIndex(index);
    }
  }, [data, params.id]);

  useEffect(() => {
    if (dataChapter?.data?.item) {
      const { chapter_image, chapter_path, chapter_name } = dataChapter.data.item;
      setImages(chapter_image || []);
      setChapterPath(chapter_path);

      toast(`Bạn đang ở chương ${chapter_name}`, {
        duration: 2000,
      });

      handleSetActivity(user, dataChapter, "readComic");
    }
  }, [dataChapter, user]);

  const handleChangeChapter = useCallback((index) => {
    setCurrentIndex(index);
    navigate(`/read/${params.slug}/${chapter[index]}`);
  }, [navigate, params.slug, chapter]);

  const handlePrevChapter = useCallback(() => {
    if (currentIndex > 0) {
      handleChangeChapter(currentIndex - 1);
    }
  }, [currentIndex, handleChangeChapter]);

  const handleNextChapter = useCallback(() => {
    if (currentIndex < chapter.length - 1) {
      handleChangeChapter(currentIndex + 1);
    }
  }, [currentIndex, chapter.length, handleChangeChapter]);

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === "ArrowLeft") {
        handlePrevChapter();
      } else if (event.key === "ArrowRight") {
        handleNextChapter();
      }
    };

    window.addEventListener("keydown", handleKeyPress);
    return () => window.removeEventListener("keydown", handleKeyPress);
  }, [handlePrevChapter, handleNextChapter]);

  const renderNavigationButtons = useCallback(() => (
    <div className="flex gap-[8px] justify-center mt-[24px]">
      <button
        onClick={handlePrevChapter}
        className={`select-none py-[4px] px-[12px] mobile:px-[8px] rounded-l-[8px] block text-lg transition-all hover:scale-[1.05] text-[#fff] ${
          currentIndex === 0
            ? "pointer-events-none bg-[rgba(16,185,129,0.48)]"
            : "pointer-events-auto bg-[#10b981]"
        }`}
      >
        <i className="mr-[8px] fa-solid fa-angle-left"></i>
        Chương trước
      </button>
      <button
        onClick={handleNextChapter}
        className={`select-none py-[4px] px-[12px] mobile:px-[8px] rounded-r-[8px] block text-lg transition-all hover:scale-[1.05] text-[#fff] ${
          currentIndex === chapter.length - 1
            ? "pointer-events-none bg-[rgba(16,185,129,0.48)]"
            : "pointer-events-auto bg-[#10b981]"
        }`}
      >
        Chương sau
        <i className="ml-[8px] fa-solid fa-angle-right"></i>
      </button>
    </div>
  ), [currentIndex, chapter.length, handlePrevChapter, handleNextChapter]);

  if (!data || !dataChapter) {
    return (
      <h4 className="text-2xl font-[600] dark:text-[#fff]">
        Đang tải dữ liệu...
      </h4>
    );
  }

  return (
    <Fragment>
      <div className="flex flex-col gap-[24px] items-center justify-center">
        <div className="bg-[rgba(16,185,129,0.15)] dark:bg-[rgba(204,204,204,0.2)] w-full p-[16px] rounded-[8px]">
          <div className="flex justify-center items-center flex-col gap-[16px] dark:text-[#fff]">
            <h4 className="lg:text-3xl mobile:text-xl font-[600] text-center">
              {`${data?.data?.item?.name} - Chương ${dataChapter?.data?.item?.chapter_name}`}
            </h4>
            {width > 1023 && (
              <p className="text-xl text-center font-[600]">
                Gợi ý: Bạn có thể sử dụng nút
                <i className="mx-[4px] fa-solid fa-arrow-left"></i> hoặc
                <i className="mx-[4px] fa-solid fa-arrow-right"></i> từ bàn
                phím để chuyển chương.
              </p>
            )}
            <span className="text-lg font-[600] text-center text-[#d90429] dark:text-[#fff]">
              Nếu truyện bị lỗi vui lòng liên hệ qua : Đào Anh Huy
              <a
                href=""
                target="_blank"
                rel="noopener"
                className="ml-[12px] underline"
              ></a>
            </span>
          </div>
          {renderNavigationButtons()}
        </div>

        <ul className="flex flex-col">
          {images.map((image, index) => (
            <li className="lg:w-[800px] h-full mobile:w-full" key={index}>
              <img
                loading="lazy"
                src={`https://sv1.otruyencdn.com/${chapterPath}/${image.image_file}`}
                alt="image"
              />
            </li>
          ))}
        </ul>

        {renderNavigationButtons()}
      </div>
    </Fragment>
  );
}

export default Read;
