import { NavLink, useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
import { useContext, useEffect, useState } from "react";

import Category from "../components/Category";
import useFetch from '../../hooks/UseFetch'
import { category } from "../../api";
import Context from "../../state/Context";
import { setScrollHidden, setScrollAuto } from "../../utils";

function SideBar() {
    const { quantityComicArchive, quantityComicHistory } = useContext(Context)
    const [isShowCategory, setIsShowCategory] = useState(false)
    const [isShowSearchPopup, setIsShowSearchPopup] = useState(false)
    const [searchQuery, setSearchQuery] = useState("")
    const { pathname } = useLocation()
    const [data] = useFetch(category)
    const navigate = useNavigate()

    useEffect(() => {
        if (isShowCategory || isShowSearchPopup) {
            setScrollHidden();
        } else {
            setScrollAuto();
        }
        
        return () => {
            setScrollAuto();
        };
    }, [isShowCategory, isShowSearchPopup]);

    const handleSearch = (e) => {
        e.preventDefault()
        if (searchQuery.trim()) {
            navigate(`/search/${searchQuery.trim()}`)
            setSearchQuery("")
            setIsShowSearchPopup(false)
        }
    }

    const handleCategoryClick = () => {
        setIsShowCategory(true);
    };
    
    const handleSearchPopupOpen = () => {
        setIsShowSearchPopup(true);
    };

    return (
        <div className="flex-shrink-0">
            <form onSubmit={handleSearch} className="mb-4">
                <div className="relative w-[80px]">
                    <input
                        type="text"
                        placeholder="Tìm..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="w-full py-2 px-2 rounded-[8px] bg-[#e8ebed] dark:bg-[rgba(204,204,204,0.2)] text-[#000] dark:text-[#fff] text-xs focus:outline-none focus:ring-2 focus:ring-[#10b981] placeholder:text-xs"
                        aria-label="Quick search"
                    />
                    <button 
                        type="submit"
                        className="absolute right-1 top-1/2 transform -translate-y-1/2 text-[#10b981]"
                        aria-label="Submit search"
                    >
                        <i className="fa-solid fa-search text-xs"></i>
                    </button>
                </div>
            </form>
            
            <ul className="sticky top-[76px] flex flex-col gap-y-[6px]">
                <li>
                    <NavLink
                        to='/'
                        className={({ isActive }) => `flex flex-col items-center justify-center w-[80px] h-[80px] gap-[8px] select-none rounded-[16px] hover:bg-[#e8ebed] dark:hover:bg-[rgba(204,204,204,0.2)] dark:hover:text-[#fff] ${pathname === '/' ? 'bg-[#e8ebed] dark:bg-[rgba(204,204,204,0.2)] dark:text-[#fff]' : 'bg-[#fff] dark:bg-transparent dark:text-[#fff]'}`}>
                        <i className="text-[20px] fa-solid fa-house"></i>
                        <span className="text-xs font-[500]">Trang chủ</span>
                    </NavLink>
                </li>
                <li>
                    <NavLink
                        to='/detail/danh-sach/truyen-moi'
                        className={({ isActive }) => `flex flex-col items-center justify-center w-[80px] h-[80px] gap-[8px] select-none rounded-[16px] hover:bg-[#e8ebed] dark:hover:bg-[rgba(204,204,204,0.2)] dark:hover:text-[#fff] ${pathname === '/detail/danh-sach/truyen-moi' ? 'bg-[#e8ebed] dark:bg-[rgba(204,204,204,0.2)] dark:text-[#fff]' : 'bg-[#fff] dark:bg-transparent dark:text-[#fff]'}`}>
                        <i className="text-[20px] fa-solid fa-rotate"></i>
                        <span className="text-xs font-[500]">Mới nhất</span>
                    </NavLink>
                </li>
                <li className="relative">
                    <NavLink
                        to='/archive'
                        className={({ isActive }) => `flex flex-col items-center justify-center w-[80px] h-[80px] gap-[8px] select-none rounded-[16px] hover:bg-[#e8ebed] dark:hover:bg-[rgba(204,204,204,0.2)] dark:hover:text-[#fff] ${pathname === '/archive' ? 'bg-[#e8ebed] dark:bg-[rgba(204,204,204,0.2)] dark:text-[#fff]' : 'bg-[#fff] dark:bg-transparent dark:text-[#fff]'}`}>
                        <i className="text-[20px] fa-regular fa-bookmark"></i>
                        <span className="text-xs font-[500]">Kho lưu trữ</span>
                    </NavLink>
                    {quantityComicArchive > 0 &&
                        <div className="absolute right-[-4px] top-[-4px] flex items-center justify-center">
                            <span className="w-[20px] h-[20px] bg-[#10b9817a] rounded-full animate-ping"></span>
                            <span className="absolute rounded-full w-[20px] h-[20px] flex items-center shadow-sm justify-center bg-[#10b981] text-[#fff] text-xs">{quantityComicArchive}</span>
                        </div>}
                </li>
                <li className="relative">
                    <NavLink
                        to='/history'
                        className={({ isActive }) => `flex flex-col items-center justify-center w-[80px] h-[80px] gap-[8px] select-none rounded-[16px] hover:bg-[#e8ebed] dark:hover:bg-[rgba(204,204,204,0.2)] dark:hover:text-[#fff] ${pathname === '/history' ? 'bg-[#e8ebed] dark:bg-[rgba(204,204,204,0.2)] dark:text-[#fff]' : 'bg-[#fff] dark:bg-transparent dark:text-[#fff]'}`}>
                        <i className="text-[20px] fa-solid fa-clock-rotate-left"></i>
                        <span className="text-xs font-[500]">Lịch sử</span>
                    </NavLink>
                    {quantityComicHistory > 0 &&
                        <div className="absolute right-[-4px] top-[-4px] flex items-center justify-center">
                            <span className="w-[20px] h-[20px] bg-[#10b9817a] rounded-full animate-ping"></span>
                            <span className="absolute rounded-full w-[20px] h-[20px] flex items-center shadow-sm justify-center bg-[#10b981] text-[#fff] text-xs">{quantityComicHistory}</span>
                        </div>}
                </li>
                <li>
                    <button 
                        onClick={handleCategoryClick}
                        className='flex flex-col items-center justify-center w-[80px] h-[80px] rounded-[16px] overflow-hidden hover:bg-[#e8ebed] dark:hover:bg-[rgba(204,204,204,0.2)] dark:hover:text-[#fff] select-none bg-[#fff] dark:bg-transparent dark:text-[#fff] gap-[8px] cursor-pointer'
                        aria-label="Open categories"
                    >
                        <i className="text-[20px] fa-solid fa-table-cells-large"></i>
                        <span className="text-xs font-[500]">Thể loại</span>
                    </button>
                </li>
                <li>
                    <button
                        onClick={handleSearchPopupOpen} 
                        className='flex flex-col items-center justify-center w-[80px] h-[80px] rounded-[16px] overflow-hidden hover:bg-[#e8ebed] dark:hover:bg-[rgba(204,204,204,0.2)] dark:hover:text-[#fff] select-none bg-[#fff] dark:bg-transparent dark:text-[#fff] gap-[8px] cursor-pointer'
                        aria-label="Advanced search"
                    >
                        <i className="text-[20px] fa-solid fa-magnifying-glass"></i>
                        <span className="text-xs font-[500]">Tìm kiếm</span>
                    </button>
                </li>
            </ul>
            
            {isShowCategory &&
                <Category
                    isShowCategory={isShowCategory}
                    setIsShowCategory={setIsShowCategory}
                    data={data}
                />}
                
            {/* Advanced Search Popup */}
            {isShowSearchPopup && (
                <div className="fixed inset-0 bg-[rgba(0,0,0,0.4)] flex items-center justify-center z-[9999]">
                    <div className="relative bg-[#fff] dark:bg-[#282828] w-full max-w-[500px] mx-[16px] rounded-[8px] p-[24px] mobile:p-[16px]">
                        <button 
                            onClick={() => setIsShowSearchPopup(false)} 
                            className="absolute top-[20px] right-[20px] mobile:top-[16px] mobile:right-[16px] cursor-pointer p-[6px] text-[#000] dark:text-[#fff] lg:text-2xl mobile:text-xl"
                            aria-label="Close search popup"
                        >
                            <i className="fa-solid fa-xmark"></i>
                        </button>
                        
                        <h3 className="lg:text-2xl mobile:text-xl font-[600] mb-[24px] text-[#000] dark:text-[#fff]">
                            Tìm kiếm nâng cao
                        </h3>
                        
                        <form onSubmit={handleSearch} className="flex flex-col gap-4">
                            <div className="relative w-full">
                                <input
                                    type="text"
                                    placeholder="Nhập tên manga cần tìm..."
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                    className="w-full py-3 px-4 pr-10 rounded-[8px] bg-[#e8ebed] dark:bg-[rgba(204,204,204,0.2)] text-[#000] dark:text-[#fff] focus:outline-none focus:ring-2 focus:ring-[#10b981]"
                                    autoFocus
                                />
                            </div>
                            
                            <button 
                                type="submit"
                                className="py-3 px-4 rounded-[8px] bg-[#10b981] text-white font-semibold hover:bg-opacity-90 transition"
                            >
                                <i className="fa-solid fa-search mr-2"></i>
                                Tìm kiếm
                            </button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default SideBar;
