import { useEffect, useState } from "react";
import { googleLogout } from "@react-oauth/google";
import Context from "./Context";

function Provider({ children }) {
    const [width, setWidth] = useState(window.innerWidth)
    const [isOpenDiaLog, setIsOpenDiaLog] = useState(false)
    const [isLogin, setIsLogin] = useState(() => {
        const isLogin = localStorage.getItem('user')
        return !!isLogin
    })
    const [user, setUser] = useState(() => {
        return JSON.parse(localStorage.getItem('user'))
    })
    const [theme, setTheme] = useState(() => {
        return localStorage.getItem('theme') || 'light'
    })
    const [quantityComicArchive, setQuantityComicArchive] = useState(() => {
        const comicStorage = JSON.parse(localStorage.getItem('comic-storage') || '{}')
        return comicStorage[user?.email]?.length
    })
    const [quantityComicHistory, setQuantityComicHistory] = useState(() => {
        const historyStorage = JSON.parse(localStorage.getItem('history-storage') || '{}')
        if (!historyStorage[user?.email]) {
            historyStorage[user?.email] = {}
        }
        return Object.keys(historyStorage[user?.email])?.length
    })

    useEffect(() => {
        setQuantityComicArchive(() => {
            const comicStorage = JSON.parse(localStorage.getItem('comic-storage') || '{}')
            return comicStorage[user?.email]?.length
        })
        setQuantityComicHistory(() => {
            const historyStorage = JSON.parse(localStorage.getItem('history-storage') || '{}')
            if (!historyStorage[user?.email]) {
                historyStorage[user?.email] = {}
            }
            return Object.keys(historyStorage[user?.email]).length
        })
    }, [user])

    useEffect(() => {
        const handleReSize = () => {
            setWidth(window.innerWidth)
        }
        window.addEventListener('resize', handleReSize)
        return () => document.removeEventListener('resize', handleReSize)
    }, [])

    useEffect(() => {
        localStorage.setItem('theme', theme)
        document.documentElement.classList.remove('light', 'dark')
        theme === 'light' ?
            document.documentElement.classList.toggle('light') :
            document.documentElement.classList.toggle('dark')
    }, [theme])

    useEffect(() => {
        setScrollDocument(isOpenDiaLog)
    }, [isOpenDiaLog])

    const handleLogout = () => {
        googleLogout()
        setUser(null)
        setIsLogin(false)
        window.location.href = '/'
        localStorage.removeItem('user')
    }

    const value = {
        isLogin,
        user,
        width,
        theme,
        isOpenDiaLog,
        quantityComicArchive,
        quantityComicHistory,
        setUser,
        setIsLogin,
        handleLogout,
        setTheme,
        setIsOpenDiaLog,
        setQuantityComicArchive,
        setQuantityComicHistory
    }

    return (
        <Context.Provider value={value}>
            {children}
        </Context.Provider>
    );
}

export default Provider;
